from util.util import Util
from bs4 import BeautifulSoup
import re
import json
import traceback


class MiExtractor(object):

    def __init__(self):
        self.util = Util()
        self.writed_content = list()

    def get_each_app_url(self, html):
        """
        解析每个app的链接
        """
        try:
            app_url_list = list()
            soup = BeautifulSoup(html, 'lxml')
            app_items = soup.select('ul.applist li > a')
            if app_items is not None:
                for item in app_items:
                    app_url_list.append('http://app.mi.com' + item.get('href'))
            return app_url_list
        except:
            print('get_each_app_url error')
            traceback.print_exc()


    def get_next_page(self, html):
        """
        解析是否含有下一页
        """
        try:
            soup = BeautifulSoup(html, 'lxml')
            page_item = soup.select('div.pages a.next')
            if page_item:
                next_page_href = 'http://app.mi.com/searchAll' + page_item[0].get('href')
                return next_page_href
        except:
            print('get_next_page error')
            traceback.print_exc()

    def extractor_info(self, url, result):
        """
        解析需要获取的内容:公司名/游戏名/分类/包名(id)
        游戏名,包名,类型,公司名,orgame,apkCode,appId,media
        """
        com_name = '-'  # 公司名
        apk_type = '-'  # 软件类别
        app_name = '-'  # 软件名
        apk_name = '-'  # 包名
        apkCode = '-'
        appId = '-'
        app_down_count = '-'
        media = url['media']
        try:
            soup = BeautifulSoup(result['result'], 'lxml')
            intro_item = soup.select('div.intro-titles')
            if intro_item:
                com_name_item = intro_item[0].select('p')
                if com_name_item:
                    com_name = com_name_item[0].text
                    apk_type = re.findall(u'分类：(.*)', com_name_item[1].text.split('|')[0])[0]
                app_name_item = intro_item[0].select('h3')
                if app_name_item:
                    app_name = app_name_item[0].text
                    apk_name = re.findall('id=(.*?)&', url['url'])[0]

            content = ','.join([app_name, apk_name, com_name, apk_type, app_down_count, apkCode, appId, media, url['keyword']])
            self.util.write_data(content)
        except:
            print('extractor_info error')
            traceback.print_exc()


